import java.util.ArrayListimport java.util.Listfun main(args: Array<String>) {    //Khai báo và gán    var name: String;    name = "Kotlin"    var tuoi: Int = 10    println(name + " " + tuoi)    //Hàm    getFullName("Nguyen", "Huy")    //Thủ tục    println(getNameFun("Pham", "Chang"))    //ArrayList    var danhsach: ArrayList<SinhVien> = ArrayList<SinhVien>()    danhsach.add(SinhVien("Ong", 13, "123", true))    danhsach.add(SinhVien("Trang", 12, "123", false))    danhsach.add(SinhVien("B", 15, "123", true))    danhsach.add(SinhVien("A", 12, "123", false))    for (s in danhsach) {        if (s.hovate.equals("Ong"))            println(s.hovate + " " + s.tuoi)    }    //List    var listds: MutableCollection<SinhVien>    listds = danhsach    println(listds.first().hovate)    //check null    var art = listds}fun getFullName(ho: String, ten: String): Unit {    println("$ho $ten");}fun getNameFun(ho: String, name: String): String {    return "$ho $name"}