package dev.nqhuy.kotlinimport java.util.*/* * Created by Chang Bee on 5/29/2017. */fun main(args: Array<String>) {    println("A-Array")    /*    * Mảng 1 chiều    * */    // Khai báo kích thước trtuowcs    val x = IntArray(3)    x[0] = 12    x[1] = 13    x[2] = 14    //Khai báo và gán giá trị luôn    val y = arrayOf("A", "B", "C")    //Các khác    var z = floatArrayOf(2f, 3f, 4f)    //Duyệt mảng    for (pt in y) {        print("$pt \t")    }    println()    //Duyet theo vi tri    for (i in 0 until y.size) {//0.1.2        print("$i = ${y[i]} \t")    }    println()    //Duyet  vi tri    for (i in y.indices) {        print("$i = ${y[i]} \t")    }    println()    /*Tích hợp sắp xếp cả java*/    val so = intArrayOf(1, 3, 0, 6, 4, 8, 2)    Arrays.sort(so)    for (pt in so)        print("Tăng: $pt \t")    println()    //Viết thuật toán sắp xếp    bubbleSort(so)    for (pt in so)        print("Giảm: $pt \t")    println()    /*    * Mảng 2 chiều    * */    println("Mảng 2 chiều: Ma trận")    val mt = arrayOf(arrayOf(10, 20, 30), arrayOf(1, 2, 3, 4, 5))// Mảng 3 dòng 5 cột    for (i in mt.indices) {        for (j in mt[i].indices) {            print(String.format("%2d \t", mt[i][j]))        }        println()    }    /*    * Collection    * */    println("B-Collection")    /*ArrayList*/    val s = arrayListOf<String>()// Thay đổi được    s.add("A")    s.add("B")    s.add("C")    s.add("F")    s.add("D")    //Duyet    // im    for (pt in s) println(pt)    //imutable    var number = listOf(1, 2, 4, 2)// Không thay đổi được    //mutable    val nb = mutableListOf<Int>()    nb.add(1)    nb.add(6)    nb.add(3)    nb.add(5)    Collections.sort(nb, { a, b -> b - a })    for (pt in nb) {        print("$pt \t")    }    // Khái niệm filter    val filted = nb.filter { it % 2 == 0 }    println("\n Lọc phân tử chia hết: \t")    for (pt in filted)        print("$pt \t")    println()    /*Hashmap: Lưu dữ liệu bằng từ khóa*/    var dic  = hashMapOf<String, String>()    dic.put("Hello", "Xin Chao")    dic.put("Morning", "Chao Buoi Sang")    dic.put("Bye", "Tam Biet")    //Duyết    for (key in dic.keys){        println(String.format("%s là %s", key, dic.get(key)))    }}fun bubbleSort(so: IntArray): Unit {    var temp: Int    for (i in so.indices) {        for (j in i + 1..so.size - 1) {            if (so[i] < so[j]) {                temp = so[i]                so[i] = so[j]                so[j] = temp            }        }    }}