package dev.nqhuy.kotlin.book/* * Created by Chang Bee on 6/2/2017. */fun main(args: Array<String>) {    /*--------------Null Safety----------------*/    //Không thể biên dịch nếu đối tượng bằng null(không thể không có giá trị)    /*var notnullArtist : Artist = null*/    //Đối tượng có thể được Null bằng cách này    //"?" nó có thể Null    var artist: Artist? = null    artist?.print()// Đối tưởng có thể null thì mới gọi thuộc tính và thông tin được    // Tương đương với    /*    if (artist !=null){        artist.print()    }    */    //Chắc chán đối tượng đó không thể bằng null    //artist!!.print()    // Toán tử Elvis đưa ra giá trị thay thế nếu đối tượng bằng null    val name = artist?.name ?: "empty"    println(name)}